print("PRESS 6 TO DRAW BLACK OUTLINE, 7 TO DRAW DARK BLUE SPOTS, 8 TO DRAW YELLOW SPOTS, 9 TO DRAW LIGHT BLUE SPOTS")


def scan(): # scan points which is used in scanning script
    spots = []
    startingpoint = (611, 295)
    endingpoint = (1019, 805)
    scanrate = 17
    for i in range(0, endingpoint[0] - startingpoint[0] + scanrate, scanrate):
        for j in range(0, endingpoint[1] - startingpoint[1] + scanrate, scanrate):
            pointcolour1 = pyautogui.pixel(startingpoint[0] + i, startingpoint[1] + j)
            percentdonex = i / (endingpoint[0] - startingpoint[0]) * 100
            percentdoney = j / (endingpoint[1] - startingpoint[1]) * 100
            if percentdoney >= 95:
                print(percentdonex, "%", percentdoney, "%")
            spots.insert(0, [(startingpoint[0] + i, startingpoint[1] + j), pointcolour1])
            # pyautogui.moveTo(startingpoint[0] + i, startingpoint[1] + j, _pause=0)
            """if pointcolour1[0] == pointcolour1[1] == pointcolour1[2]:
                if pointcolour1[0] <= 100:
                    #print(i, j, pointcolour1)
                    spots.insert(0,[startingpoint[0] + i, startingpoint[1] + j])
                    #print(spots[0])"""
    print("scan completed!")
    print(spots)

#time.sleep(5)
