def drawBlack(): # draw black spots
    for x in range(0, len(spots)): # for all the spots, check if the spot is black
        if spots[x][1][0] + spots[x][1][1] + spots[x][1][2] <= 12: #if the spot has all RGB values <= 12 , means very dark spot
            pyautogui.click(spots[x][0], _pause=False) #click the spot(x and y coordinates)

def drawDarkblue(): # draw dark blue spots
    for x in range(0, len(spots)): # for all the spots
        check1 = abs(spots[x][1][0] - (14)) #check Red value difference with dark blue
        check2 = abs(spots[x][1][1] - (71)) #check Green value difference with dark blue
        check3 = abs(spots[x][1][2] - (161)) #check Blue value difference with dark blue
        if check1 <= 10 and check2 <= 10 and check3 <= 10: #if all RGB value is <=10, means colour is very close to dark blue 
            pyautogui.click(spots[x][0], _pause=False) #click on spot

def drawLightblue(): # draw light blue spots, same procedure as above
    for x in range(0, len(spots)):
        check1 = abs(spots[x][1][0] - (2))
        check2 = abs(spots[x][1][1] - (169))
        check3 = abs(spots[x][1][2] - (245))
        if check1 <= 3 and check2 <= 3 and check3 <= 3:
            pyautogui.click(spots[x][0], _pause=False)

def drawYellow(): # draw yellow spots, same procedure as above
    for x in range(0, len(spots)):
        check1 = abs(spots[x][1][0] - (255))
        check2 = abs(spots[x][1][1] - (151))
        check3 = abs(spots[x][1][2] - (0))
        if check1 <= 10 and check2 <= 10 and check3 <= 10:
            pyautogui.click(spots[x][0], _pause=False)

def drawNotBlackorWhite(): # not used anymore
    for x in range(0, len(spots)):
        if spots[x][1][0] + spots[x][1][1] + spots[x][1][2] > 6 and spots[x][1][0] + spots[x][1][1] + spots[x][1][2] < 750:
            pyautogui.click(spots[x][0], _pause=False)

